* {
    border: none;
    border-radius: 0;
    font-family: "JetBrainsMono Nerd Font", "Noto Sans CJK SC", sans-serif;
    font-weight: bold;
    font-size: 14px;
    min-height: 0;
}

window#waybar {
    background: rgba(21, 18, 27, 0.9);
    color: #cdd6f4;
    transition-property: background-color;
    transition-duration: .5s;
    border-bottom: 3px solid rgba(100, 114, 125, 0.5);
}

window#waybar.hidden {
    opacity: 0.2;
}

#workspaces {
    margin: 0 4px;
}

#workspaces button {
    padding: 0 10px;
    background-color: transparent;
    color: #ffffff;
    border: none;
    border-radius: 16px;
    box-shadow: inset 0 -3px transparent;
    transition: all 0.3s ease-in-out;
}

#workspaces button.active {
    background: rgba(245, 194, 231, 0.2);
    color: #f5c2e7;
    box-shadow: inset 0 -3px #f5c2e7;
}

#workspaces button:hover {
    background: rgba(245, 194, 231, 0.1);
    color: #f5c2e7;
}

#workspaces button.urgent {
    background: rgba(243, 139, 168, 0.2);
    color: #f38ba8;
    box-shadow: inset 0 -3px #f38ba8;
}

#custom-launcher {
    color: #89b4fa;
    background: rgba(137, 180, 250, 0.1);
    border-radius: 16px;
    padding: 0 12px;
    margin: 0 4px;
    transition: all 0.3s ease-in-out;
}

#custom-launcher:hover {
    background: rgba(137, 180, 250, 0.2);
}

#window {
    margin: 0 4px;
    padding: 0 10px;
    border-radius: 16px;
    background: rgba(116, 199, 236, 0.1);
    color: #74c7ec;
    transition: all 0.3s ease-in-out;
}

#clock {
    color: #fab387;
    background: rgba(250, 179, 135, 0.1);
    border-radius: 16px;
    padding: 0 16px;
    margin: 0 4px;
    transition: all 0.3s ease-in-out;
}

#clock:hover {
    background: rgba(250, 179, 135, 0.2);
}

#network {
    color: #a6e3a1;
    background: rgba(166, 227, 161, 0.1);
    border-radius: 16px;
    padding: 0 12px;
    margin: 0 4px;
    transition: all 0.3s ease-in-out;
}

#network:hover {
    background: rgba(166, 227, 161, 0.2);
}

#bluetooth {
    color: #89b4fa;
    background: rgba(137, 180, 250, 0.1);
    border-radius: 16px;
    padding: 0 12px;
    margin: 0 4px;
    transition: all 0.3s ease-in-out;
}

#bluetooth:hover {
    background: rgba(137, 180, 250, 0.2);
}

#pulseaudio {
    color: #f9e2af;
    background: rgba(249, 226, 175, 0.1);
    border-radius: 16px;
    padding: 0 12px;
    margin: 0 4px;
    transition: all 0.3s ease-in-out;
}

#pulseaudio:hover {
    background: rgba(249, 226, 175, 0.2);
}

#pulseaudio.microphone {
    color: #cba6f7;
    background: rgba(203, 166, 247, 0.1);
    border-radius: 16px;
    padding: 0 12px;
    margin: 0 4px;
    transition: all 0.3s ease-in-out;
}

#pulseaudio.microphone:hover {
    background: rgba(203, 166, 247, 0.2);
}

#battery {
    color: #a6e3a1;
    background: rgba(166, 227, 161, 0.1);
    border-radius: 16px;
    padding: 0 12px;
    margin: 0 4px;
    transition: all 0.3s ease-in-out;
}

#battery:hover {
    background: rgba(166, 227, 161, 0.2);
}

#battery.charging {
    color: #f9e2af;
    background: rgba(249, 226, 175, 0.1);
}

#battery.warning:not(.charging) {
    color: #fab387;
    background: rgba(250, 179, 135, 0.1);
}

#battery.critical:not(.charging) {
    color: #f38ba8;
    background: rgba(243, 139, 168, 0.1);
    animation: blink 0.5s linear infinite alternate;
}

@keyframes blink {
    to {
        background-color: rgba(243, 139, 168, 0.3);
    }
}

#custom-power {
    color: #f38ba8;
    background: rgba(243, 139, 168, 0.1);
    border-radius: 16px;
    padding: 0 12px;
    margin: 0 4px;
    transition: all 0.3s ease-in-out;
}

#custom-power:hover {
    background: rgba(243, 139, 168, 0.2);
}

tooltip {
    background: rgba(21, 18, 27, 0.9);
    border-radius: 10px;
    border-width: 2px;
    border-style: solid;
    border-color: rgba(100, 114, 125, 0.5);
}

tooltip label {
    color: #cdd6f4;
}

